/**
 * 书籍页面JavaScript文件
 * 主要功能：
 * 1. 主题切换功能
 * 2. 书籍弹窗交互
 * 3. 书籍内容加载和阅读位置记忆
 */

// 初始化Clarity分析工具
const toggleSwitch = document.getElementById("theme-toggle");

/**
 * 检测系统是否为深色模式
 * @type {boolean}
 */
const prefersDarkMode = window.matchMedia("(prefers-color-scheme: dark)").matches;

// 根据系统偏好设置初始主题
if (prefersDarkMode) {
    document.body.classList.remove("light");
    document.body.classList.add("dark");
    toggleSwitch.checked = true;

    // 初始化弹窗颜色为深色
    const modal = document.querySelector('.modal-content');
    modal.style.backgroundColor = '#333';
    modal.style.color = '#fff';
}

/**
 * 添加书籍点击事件
 * 点击书籍项时显示弹窗并加载对应书籍内容
 */
document.querySelectorAll('.book-item').forEach((item, index) => {
    item.style.cursor = 'pointer'; // 设置鼠标指针样式

    item.addEventListener('click', function () {
        const modal = document.querySelector('.book-modal');
        modal.style.display = 'block';

        // 延迟添加show类以实现动画效果
        setTimeout(() => {
            modal.classList.add('show');
        }, 10);

        // 根据点击的书籍索引加载对应内容
        if (index === 0) {
            loadBookContent('kongyiji'); // 加载孔乙己内容
        } else if (index === 1) {
            loadBookContent('daocaoren'); // 加载稻草人内容
        } else if (index === 2) {
            loadBookContent('jiyiheishifanmaizhe'); 
        }
    });
});

// 关闭弹窗事件
document.querySelector('.close-modal').addEventListener('click', function () {
    const modal = document.querySelector('.book-modal');
    modal.classList.remove('show');

    // 延迟隐藏弹窗以实现淡出动画
    setTimeout(() => {
        modal.style.display = 'none';
    }, 300);
});

/**
 * 主题切换事件监听
 * 切换深色/浅色主题并更新相关元素样式
 */
toggleSwitch.addEventListener("change", () => {
    document.body.classList.toggle("dark");
    document.body.classList.toggle("light");

    // 更新弹窗和书籍介绍的颜色主题
    updateThemeColors();
});

/**
 * 加载书籍内容函数
 * @param {string} bookId - 书籍ID (kongyiji或daocaoren)
 */
